import{toast as t}from"../helper/config.js";class r{static async getReportSalesByMonth(){const a=await fetch("api/analytics/report/month",{method:"GET",headers:{"Content-Type":"application/json"},credentials:"include"});return a.status===200?(await a.json()).data:(t({message:"Gagal mendapatkan laporan penjualan",type:"error"}),[])}static async getReportSalesByYear(){const a=await fetch("api/analytics/report/year",{method:"GET",headers:{"Content-Type":"application/json"},credentials:"include"});return a.status===200?(await a.json()).data[0]:(t({message:"Gagal mendapatkan laporan penjualan tahunan",type:"error"}),{total_profit:0,total_sales:0,total_revenue:0})}static async getReportSalesByCategory(){const a=await fetch("api/analytics/report/category",{method:"GET",headers:{"Content-Type":"application/json"},credentials:"include"});if(a.status===200){const e=await a.json();return console.log(e.data),e.data}else return t({message:"Gagal mendapatkan laporan penjualan per katergory",type:"error"}),[]}}export{r as default};
